mapper.select:
SELECT calendarno, labeldate, label, title, content, cnt, rdate
FROM calendar
WHERE calendarno = #{calendarno}

mapper.update:
UPDATE calendar
SET labeldate=#{labeldate}, label=#{label}, title=#{title}, content=#{content}
WHERE calendarno = #{calendarno}

mapper.select:
    SELECT calendarno, labeldate, label
    FROM calendar
    WHERE labeldate = #{labeldate}
