mapper.update:
update images set;         
ansnum = ansnum + 1;       
where grpno = #{grpno} and ansnum > #{ansnum}

mapper.select:
SELECT COUNT(no) as cnt 
FROM images
WHERE no=#{no} AND passwd=#{passwd}

mapper.update:
UPDATE images
 
wname=#{wname}, title=#{title}, content=#{content},

fname=#{fname}


where no = #{no}
